$("body.item .entry-body a img").each(function(){
    var lnkButton=$(this).parent(),
        lnkUrl=lnkButton.attr('href');
	if(lnkUrl != undefined) {
	  lnkButton.attr('data-lightbox','img-post');
	}
});
